INT.value:
{
my $r= (ReadingsVal("FritzBox","box_rateDown",0));
fhem("set FB_down $r");

my $u= (ReadingsVal("FritzBox","box_rateUp",0));
fhem("set FB_up $u");

my $c= (ReadingsVal("FritzBox","box_wlanCount",0));
fhem("set FB_wlan $c");

my $p= (ReadingsVal("FritzBox","box_powerRate",0));
fhem("set FB_power $p");

my $t= (ReadingsVal("FritzBox","box_cpuTemp",0));
fhem("set FB_temp $t");

}

+*00:05:00 {
my $r= (ReadingsVal("FritzBox","box_rateDown",0));
fhem("set FB_down $r");

my $u= (ReadingsVal("FritzBox","box_rateUp",0));
fhem("set FB_up $u");

my $c= (ReadingsVal("FritzBox","box_wlanCount",0));
fhem("set FB_wlan $c");

my $p= (ReadingsVal("FritzBox","box_powerRate",0));
fhem("set FB_power $p");

my $t= (ReadingsVal("FritzBox","box_cpuTemp",0));
fhem("set FB_temp $t");

}

{
my $w= (ReadingsVal("FritzBox","box_wlan_5GHz","off"));
fhem("set FB_WLAN5 $w");

my $w2= (ReadingsVal("FritzBox","box_wlan_2.4GHz","on"));
fhem("set FB_WLAN24 $w2");

my $g= (ReadingsVal("FritzBox","box_guestWlan","off"));
fhem("set FB_WLAN_G $g");

my $ab= (ReadingsVal("FritzBox","tam1_state","off"));
fhem("set FB_AB $ab");

my $ms= (ReadingsVal("FritzBox","tam1_newMsg",0));
fhem("set FB_msg $ms");
}

+*00:05:00 {
my $w= (ReadingsVal("FritzBox","box_wlan_5GHz","off"));
fhem("set FB_WLAN5 $w");

my $w2= (ReadingsVal("FritzBox","box_wlan_2.4GHz","on"));
fhem("set FB_WLAN24 $w2");

my $g= (ReadingsVal("FritzBox","box_guestWlan","off"));
fhem("set FB_WLAN_G $g");

my $ab= (ReadingsVal("FritzBox","tam1_state","off"));
fhem("set FB_AB $ab");

my $ms= (ReadingsVal("FritzBox","tam1_newMsg",0));
fhem("set FB_msg $ms");
}

{
my $d= ((ReadingsVal("FritzBox",".box_TodayBytesReceivedLow",0) / (1000*1000) ) / 1000 + ( ReadingsVal("FritzBox",".box_TodayBytesReceivedHigh",0) * 4294967296 / (1000*1000) ) / 1000);
fhem("set Empfangen $d");

my $e= ((ReadingsVal("FritzBox",".box_TodayBytesSentLow",0) / (1000*1000) ) / 1000 + ( ReadingsVal("FritzBox",".box_TodayBytesSentHigh",0) * 4294967296 / (1000*1000) ) / 1000);
fhem("set Gesendet $e");
}

+*00:10:00 {
my $d= ((ReadingsVal("FritzBox",".box_TodayBytesReceivedLow",0) / (1000*1000) ) / 1000 + ( ReadingsVal("FritzBox",".box_TodayBytesReceivedHigh",0) * 4294967296 / (1000*1000) ) / 1000);
fhem("set Empfangen $d");

my $e= ((ReadingsVal("FritzBox",".box_TodayBytesSentLow",0) / (1000*1000) ) / 1000 + ( ReadingsVal("FritzBox",".box_TodayBytesSentHigh",0) * 4294967296 / (1000*1000) ) / 1000);
fhem("set Gesendet $e");
}

{
## fhem "set NexusAndyAMAD volume 15; sleep 5; set NexusAndyAMAD ttsMsg Andy hat das Gebäude verlassen.";
fhem "set Andy:FILTER=STATE!=absent absent";
}

