}

Macro_Andy_asleep {

fhem "set NexusAndyAMAD volume 10; sleep 5; set NexusAndyAMAD ttsMsg Andy ist ins Bett gegangen und schläft.";
fhem "set Moto2AndyAMAD screen off";
fhem "set NexusAndy2AMAD screen off";
fhem "set NexusAndyAMAD screen off";
fhem "set LED_SZ off";
fhem "set wd_Andy_F_Abwesend inactive";
fhem "set MotoAndyAMAD volumeNotification 0";

}

{

for (my $i=1; $i <= 15; $i++) {
	if (defined($defs{"atTmp_" . $i . "_" . $NAME})) {
    	fhem "delete atTmp_" . $i . "_" . $NAME;
	}
}

fhem "set Andy:FILTER=STATE!=home home";
fhem "set Lampe_SZ off";
fhem "set LED_SZ off";
fhem "set Laden_SZ off";
fhem "set MotoAndyAMAD mediaGoogleMusic stop";
fhem "set NexusAndyAMAD volume 15; sleep 5; set NexusAndyAMAD ttsMsg Andy ist aufgestanden.";
fhem "set wd_Andy_F_Abwesend active";
fhem "set MotoAndyAMAD volumeNotification 7";
fhem "sleep 5; set Moto2AndyAMAD screen off";

}

Macro_Andy_awoken {

for (my $i=1; $i <= 15; $i++) {
	if (defined($defs{"atTmp_" . $i . "_" . $NAME})) {
    	fhem "delete atTmp_" . $i . "_" . $NAME;
	}
}

fhem "set Andy:FILTER=STATE!=home home";
fhem "set Lampe_SZ off";
fhem "set LED_SZ off";
fhem "set Laden_SZ off";
fhem "set MotoAndyAMAD mediaGoogleMusic stop";
fhem "set NexusAndyAMAD volume 15; sleep 5; set NexusAndyAMAD ttsMsg Andy ist aufgestanden.";
fhem "set wd_Andy_F_Abwesend active";
fhem "set MotoAndyAMAD volumeNotification 7";
fhem "sleep 5; set Moto2AndyAMAD screen off";

}

{

fhem "set NexusAndyAMAD volume 10; sleep 5; set NexusAndyAMAD ttsMsg Andy geht jetzt ins Bett";
fhem "set Laden_SZ on";
my $KarinHome = ReadingsVal("Karin","state","home");
if ($KarinHome ne "home") {
	fhem "set LED_SZ on; sleep 10; set LED_SZ dimup";
}
fhem "set Moto2AndyAMAD screen on";
fhem "set NexusAndy2AMAD screen on";

my $nextWakeup = ReadingsVal("Andy","nextWakeup","none");
my $text = "Es ist kein Wecker gestellt. Du könntest verschlafen! Trotzdem eine gute Nacht.";

if ($nextWakeup ne "OFF") {
	$text = "Dein Wecker ist auf $nextWakeup Uhr gestellt. Gute Nacht und schlaf gut.";
}

my $HandyPower = ReadingsVal("MotoAndyAMAD","powerLevel","0");

if (ReadingsVal("MotoAndyAMAD","powerPlugged","0") == 0 && ReadingsVal("MotoAndyAMAD","powerLevel","0") < 50) {
	$text = "Dein Wecker ist zwar auf $nextWakeup Uhr gestellt, aber dein Handy ist nicht angesteckt und hat nur noch $HandyPower Prozent Kapazität, bitte anstecken. Gute Nacht und schlaf gut.";
}

if ($nextWakeup ne "none") {
	fhem "{ EveningMotoMsg(\"Andy\") }; sleep 1; set MotoAndyAMAD ttsMsg $text";
}

}

Macro_Andy_gotosleep {

fhem "set NexusAndyAMAD volume 10; sleep 5; set NexusAndyAMAD ttsMsg Andy geht jetzt ins Bett";
fhem "set Laden_SZ on";
my $KarinHome = ReadingsVal("Karin","state","home");
if ($KarinHome ne "home") {
	fhem "set LED_SZ on; sleep 10; set LED_SZ dimup";
}
fhem "set Moto2AndyAMAD screen on";
fhem "set NexusAndy2AMAD screen on";

my $nextWakeup = ReadingsVal("Andy","nextWakeup","none");
my $text = "Es ist kein Wecker gestellt. Du könntest verschlafen! Trotzdem eine gute Nacht.";

if ($nextWakeup ne "OFF") {
	$text = "Dein Wecker ist auf $nextWakeup Uhr gestellt. Gute Nacht und schlaf gut.";
}

my $HandyPower = ReadingsVal("MotoAndyAMAD","powerLevel","0");

if (ReadingsVal("MotoAndyAMAD","powerPlugged","0") == 0 && ReadingsVal("MotoAndyAMAD","powerLevel","0") < 50) {
	$text = "Dein Wecker ist zwar auf $nextWakeup Uhr gestellt, aber dein Handy ist nicht angesteckt und hat nur noch $HandyPower Prozent Kapazität, bitte anstecken. Gute Nacht und schlaf gut.";
}

if ($nextWakeup ne "none") {
	fhem "{ EveningMotoMsg(\"Andy\") }; sleep 1; set MotoAndyAMAD ttsMsg $text";
}

}

{
my $anwesend = ReadingsVal("GroegerBande","residentsHomeDevs","niemand");
my $anzahl = ReadingsVal("GroegerBande","residentsHome","0");
my $text = "";

if($anzahl <=  1) { $text = "ist"; } else { $text = "sind"; }

if($anzahl ==  0) { $anwesend = "niemand"; }

## fhem "set NexusAndyAMAD volume 10; sleep 5; set NexusAndyAMAD ttsMsg Hallo Andy, willkommen zuhause. Außer dir $text noch $anwesend zuhause";
fhem "set Andy:FILTER=STATE=absent home";
}

Macro_Andy_home {
my $anwesend = ReadingsVal("GroegerBande","residentsHomeDevs","niemand");
my $anzahl = ReadingsVal("GroegerBande","residentsHome","0");
my $text = "";

if($anzahl <=  1) { $text = "ist"; } else { $text = "sind"; }

if($anzahl ==  0) { $anwesend = "niemand"; }

## fhem "set NexusAndyAMAD volume 10; sleep 5; set NexusAndyAMAD ttsMsg Hallo Andy, willkommen zuhause. Außer dir $text noch $anwesend zuhause";
fhem "set Andy:FILTER=STATE=absent home";
}

{
##
## Actual FHEM commands are commented out by default as they would need
## to be adapted to your configuration.
##
## Available wake-up variables:
## 1. $EVTPART0 -> start or stop
## 2. $EVTPART1 -> target wake-up time
## 3. $EVTPART2 -> wake-up begin time considering wakeupOffset attribute
## 4. $EVTPART3 -> enforced wakeup yes=1,no=0 from wakeupEnforced attribute
## 5. $EVTPART4 -> device name of the user which called this macro
## 6. $EVTPART5 -> current state of user
##=============================================================================
## DELETE TEMP. AT-COMMANDS POTENTIALLY CREATED EARLIER BY THIS SCRIPT
## Executed for start to cleanup in case this wake-up automation is re-started.
## Executed for stop to cleanup in case the user ends this automation earlier.
##
for (my $i=1; $i <= 15; $i++) {
	if (defined($defs{"atTmp_" . $i . "_" . $NAME})) {
    	fhem "delete atTmp_" . $i . "_" . $NAME;
	}
}

##-----------------------------------------------------------------------------
## BEGIN WAKE-UP PROGRAM
## Run first automation commands and create temp. at-devices for lagging actions.
##
if ($EVTPART0 eq "start") {
	Log3 $NAME, 3, "$NAME: Wake-up program started for $EVTPART4 with target time $EVTPART1. Current state: $EVTPART5";
	fhem "define atTmp_1_$NAME at +00:01:00 set LED_SZ on;; sleep 2;; set LED_SZ HSV 0,100,50 240;; set Moto2AndyAMAD screen on";
	fhem "define atTmp_2_$NAME at +00:05:00 set LED_SZ HSV 0,100,80 240";
	fhem "define atTmp_3_$NAME at +00:10:00 set LED_SZ HSV 40,100,80 240";
	fhem "define atTmp_4_$NAME at +00:15:00 set LED_SZ HSV 0,0,50 240";
	fhem "define atTmp_5_$NAME at +00:15:30 set MotoAndyAMAD volume 1;; sleep 1;; set MotoAndyAMAD mediaGoogleMusic play/pause";
	fhem "define atTmp_6_$NAME at +00:17:30 set MotoAndyAMAD volume 2";
	fhem "define atTmp_7_$NAME at +00:19:30 set MotoAndyAMAD volume 2";
	fhem "define atTmp_8_$NAME at +00:20:00 set LED_SZ HSV 0,0,80 240";
	fhem "define atTmp_9_$NAME at +00:30:00 set MotoAndyAMAD mediaGoogleMusic play/pause;; sleep 1;; set MotoAndyAMAD volume 7;; sleep 1;; set MotoAndyAMAD ttsMsg Guten Morgen Andy, es ist " . $EVTPART1 . " Uhr, jetzt ist es Zeit zum aufstehen!";

	# if wake-up should be enforced
	if ($EVTPART3) {
		Log (4, "$NAME: planning enforced wake-up");
		fhem "define atTmp_10_$NAME at +00:25:00 set MotoAndyAMAD mediaGoogleMusic play/pause;; sleep 1;; set LED_SZ HSV 0,0,100 120;; set MotoAndyAMAD volume 7;; sleep 1;; set MotoAndyAMAD ttsMsg Guten Morgen Andy, es ist gleich " . $EVTPART1 . " Uhr, du solltest langsam aufstehen!;; sleep 2;; set MotoAndyAMAD volume 2;; sleep 1;; set MotoAndyAMAD mediaGoogleMusic play/pause";
		fhem "define atTmp_11_$NAME at +00:27:30 set MotoAndyAMAD mediaGoogleMusic play/pause;; sleep 1;; \{ MorningMotoMsg(\"Andy\") \};; sleep 1;; set MotoAndyAMAD volume 2;; sleep 1;; set MotoAndyAMAD mediaGoogleMusic play/pause";
		}
}
##-----------------------------------------------------------------------------
## END WAKE-UP PROGRAM (OPTIONAL)
## Put some post wake-up tasks here like reminders after the actual wake-up period.
##
## Note: Will only be run when program ends normally after minutes specified in wakeupOffset.
##       If stop was user-forced by sending explicit set-command 'stop', this is not executed
##       assuming the user does not want any further automation activities.
##
if ($EVTPART0 eq "stop") {
	Log3 $NAME, 3, "$NAME: Wake-up program ended for $EVTPART4 with target time $EVTPART1. Current state: $EVTPART5";
	fhem "define atTmp_12_$NAME at +00:01:00 set LED_SZ dim 50 60;; sleep 2;; set MotoAndyAMAD mediaGoogleMusic stop;; sleep 2;; set LED_SZ dim 0 60";

	# if wake-up should be enforced, auto-change user state from 'asleep' to 'awoken'
	# after a small additional nap to kick you out of bed if user did not confirm to be awake :-)
	# An additional notify for user state 'awoken' may take further actions
	# and change to state 'home' afterwards.
	if ($EVTPART3) {
		fhem "define atTmp_13_$NAME at +00:05:00 set $EVTPART4:FILTER=STATE=asleep awoken";

	# Without enforced wake-up, be jentle and just set user state to 'home' after some
	# additional long nap time
	} else {
		fhem "define atTmp_14_$NAME at +00:15:00 set $EVTPART4:FILTER=STATE=asleep home";
		fhem "define atTmp_15_$NAME at +00:05:00 set LED_SZ dim 50 60;; sleep 2;; set MotoAndyAMAD mediaGoogleMusic stop;; sleep 2;; set LED_SZ dim 0 60";
    }
}
}

Macro_Andy_wakeuptimer1 {
##
## Actual FHEM commands are commented out by default as they would need
## to be adapted to your configuration.
##
## Available wake-up variables:
## 1. $EVTPART0 -> start or stop
## 2. $EVTPART1 -> target wake-up time
## 3. $EVTPART2 -> wake-up begin time considering wakeupOffset attribute
## 4. $EVTPART3 -> enforced wakeup yes=1,no=0 from wakeupEnforced attribute
## 5. $EVTPART4 -> device name of the user which called this macro
## 6. $EVTPART5 -> current state of user
##=============================================================================
## DELETE TEMP. AT-COMMANDS POTENTIALLY CREATED EARLIER BY THIS SCRIPT
## Executed for start to cleanup in case this wake-up automation is re-started.
## Executed for stop to cleanup in case the user ends this automation earlier.
##
for (my $i=1; $i <= 15; $i++) {
	if (defined($defs{"atTmp_" . $i . "_" . $NAME})) {
    	fhem "delete atTmp_" . $i . "_" . $NAME;
	}
}

##-----------------------------------------------------------------------------
## BEGIN WAKE-UP PROGRAM
## Run first automation commands and create temp. at-devices for lagging actions.
##
if ($EVTPART0 eq "start") {
	Log3 $NAME, 3, "$NAME: Wake-up program started for $EVTPART4 with target time $EVTPART1. Current state: $EVTPART5";
	fhem "define atTmp_1_$NAME at +00:01:00 set LED_SZ on;; sleep 2;; set LED_SZ HSV 0,100,50 240;; set Moto2AndyAMAD screen on";
	fhem "define atTmp_2_$NAME at +00:05:00 set LED_SZ HSV 0,100,80 240";
	fhem "define atTmp_3_$NAME at +00:10:00 set LED_SZ HSV 40,100,80 240";
	fhem "define atTmp_4_$NAME at +00:15:00 set LED_SZ HSV 0,0,50 240";
	fhem "define atTmp_5_$NAME at +00:15:30 set MotoAndyAMAD volume 1;; sleep 1;; set MotoAndyAMAD mediaGoogleMusic play/pause";
	fhem "define atTmp_6_$NAME at +00:17:30 set MotoAndyAMAD volume 2";
	fhem "define atTmp_7_$NAME at +00:19:30 set MotoAndyAMAD volume 2";
	fhem "define atTmp_8_$NAME at +00:20:00 set LED_SZ HSV 0,0,80 240";
	fhem "define atTmp_9_$NAME at +00:30:00 set MotoAndyAMAD mediaGoogleMusic play/pause;; sleep 1;; set MotoAndyAMAD volume 7;; sleep 1;; set MotoAndyAMAD ttsMsg Guten Morgen Andy, es ist " . $EVTPART1 . " Uhr, jetzt ist es Zeit zum aufstehen!";

	# if wake-up should be enforced
	if ($EVTPART3) {
		Log (4, "$NAME: planning enforced wake-up");
		fhem "define atTmp_10_$NAME at +00:25:00 set MotoAndyAMAD mediaGoogleMusic play/pause;; sleep 1;; set LED_SZ HSV 0,0,100 120;; set MotoAndyAMAD volume 7;; sleep 1;; set MotoAndyAMAD ttsMsg Guten Morgen Andy, es ist gleich " . $EVTPART1 . " Uhr, du solltest langsam aufstehen!;; sleep 2;; set MotoAndyAMAD volume 2;; sleep 1;; set MotoAndyAMAD mediaGoogleMusic play/pause";
		fhem "define atTmp_11_$NAME at +00:27:30 set MotoAndyAMAD mediaGoogleMusic play/pause;; sleep 1;; \{ MorningMotoMsg(\"Andy\") \};; sleep 1;; set MotoAndyAMAD volume 2;; sleep 1;; set MotoAndyAMAD mediaGoogleMusic play/pause";
		}
}
##-----------------------------------------------------------------------------
## END WAKE-UP PROGRAM (OPTIONAL)
## Put some post wake-up tasks here like reminders after the actual wake-up period.
##
## Note: Will only be run when program ends normally after minutes specified in wakeupOffset.
##       If stop was user-forced by sending explicit set-command 'stop', this is not executed
##       assuming the user does not want any further automation activities.
##
if ($EVTPART0 eq "stop") {
	Log3 $NAME, 3, "$NAME: Wake-up program ended for $EVTPART4 with target time $EVTPART1. Current state: $EVTPART5";
	fhem "define atTmp_12_$NAME at +00:01:00 set LED_SZ dim 50 60;; sleep 2;; set MotoAndyAMAD mediaGoogleMusic stop;; sleep 2;; set LED_SZ dim 0 60";

	# if wake-up should be enforced, auto-change user state from 'asleep' to 'awoken'
	# after a small additional nap to kick you out of bed if user did not confirm to be awake :-)
	# An additional notify for user state 'awoken' may take further actions
	# and change to state 'home' afterwards.
	if ($EVTPART3) {
		fhem "define atTmp_13_$NAME at +00:05:00 set $EVTPART4:FILTER=STATE=asleep awoken";

	# Without enforced wake-up, be jentle and just set user state to 'home' after some
	# additional long nap time
	} else {
		fhem "define atTmp_14_$NAME at +00:15:00 set $EVTPART4:FILTER=STATE=asleep home";
		fhem "define atTmp_15_$NAME at +00:05:00 set LED_SZ dim 50 60;; sleep 2;; set MotoAndyAMAD mediaGoogleMusic stop;; sleep 2;; set LED_SZ dim 0 60";
    }
}
}

{
## fhem "set NexusAndyAMAD volume 15; sleep 5; set NexusAndyAMAD ttsMsg Niemand mehr zuhause, alle Stromverbraucher und WLAN werden ausgeschaltet.";
fhem "set Pushbullet message Niemand mehr zuhause, alles aus. | Abwesenheitsmeldung | Moto G";
fhem "set Lampe_WZ off";
fhem "set Laden_SZ off";
fhem "set Lampe_SZ off";
