			/* <![CDATA[ */
			jQuery('#topUserName').attr('placeholder', 'Username');
			jQuery('#topPassword').attr('placeholder', 'Passwort');

			var major = Math.floor(jQuery.browser.version);
			jQuery('#topLoginForm input').each(function(index, item)
			{
				var jEl = jQuery(item);

				if(jQuery.browser.msie && jQuery.browser.version < 10.0) {
					jEl.focus(function(event)
					{
						if(jEl.attr('value') == jEl.attr('placeholder')) {
							if(jEl.hasClass('password') && major == 9) {
								jEl[0].type = 'password';
							}
							jEl.attr('value', '');
						}
					});
					jEl.blur(function(event)
					{
						if(!jEl.attr('value')) {
							if(jEl.hasClass('password') && major == 9) {
								jEl[0].type = 'text';
							}
							jEl.attr('value', jEl.attr('placeholder'));
						}
					});
					jEl.blur();
				}
				else {
					jEl.focus(function(event)
					{
						if (!jEl.data("placeholder")) {
							jEl.data("placeholder", jEl.attr('placeholder'));
						}
						jEl.attr('placeholder', '');
					});
					jEl.blur(function(event)
					{
						jEl.attr('placeholder', jEl.data("placeholder"));
					});
					jEl.blur();
				}
			});
			/* ]]> */
		</script>
