		var SearchModule = (function(thiz)
		{
			thiz.search = function()
			{
				var url = '/programmsuche.html';
				var title = jQuery('#searchModule input[type=text]').val();
				location.href = '/programmsuche.html?search=1&title=' + (title != "Schnellsuche" ? title : "");
			};

			thiz.init = function ()
			{
				jQuery("#pit5a258eea5b291").focus(thiz.simulatePlaceholder).blur(thiz.simulatePlaceholder);
				jQuery('#searchModule a').click(function(event)
				{
					thiz.search();
				});
			};

			thiz.simulatePlaceholder = function (event)
			{
				var jQS = jQuery("#pit5a258eea5b291");
				var value = jQS.val();

				if (value == "Schnellsuche" || !value.length) {
					jQS.val((event.type == "focus" ? "" : "Schnellsuche"));
				}
			};

			return thiz;
		})({});
		SearchModule.init();
