ATTR.value:
{
	#if($READING eq "now_text" && $VALUE =~ "Restabfall") { 'style="color:black"' }
	if($READING eq "now_text" && $VALUE =~ "Gelber Sack") { 'style="color:yellow"' }
	elsif($READING eq "now_text" && $VALUE =~ "Altpapier") { 'style="color:green"' }
	#elsif($READING eq "next_text" && $VALUE =~ "Restabfall") { 'style="color:black"' }
	elsif($READING eq "next_text" && $VALUE =~ "Gelber Sack") { 'style="color:yellow"' }
	elsif($READING eq "next_text" && $VALUE =~ "Altpapier") { 'style="color:green"' }
}

INT.value:
{
	if ((Value("GLOBALANWESENHEIT") eq "present") && (Value("Automatik") eq "Ein")) {
		fhem("set ColorUpdate push");
	}
	else {
		if (ReadingsVal("LED2","state",'') ne "off") {
			fhem("set ColorUpdate push");
		}
	}
}

+*00:01:00 {
	if ((Value("GLOBALANWESENHEIT") eq "present") && (Value("Automatik") eq "Ein")) {
		fhem("set ColorUpdate push");
	}
	else {
		if (ReadingsVal("LED2","state",'') ne "off") {
			fhem("set ColorUpdate push");
		}
	}
}

{
	my $alexa=`pgrep alexa`;
	my $lcs=`ps ax | grep ld382a.py | grep -v grep | cut -d" " -f1`;
	if ($alexa ||= ""){
		fhem("attr ALEXASERVICE devStateIcon .*:general_an\@green");
		fhem("setreading ALEXASERVICE processid $alexa");
	}
	else {
		fhem("attr ALEXASERVICE devStateIcon .*:general_aus\@red");
		fhem("setreading ALEXASERVICE processid - - -");
		fhem("set ALEXASERVICE off");
	}
	if ($lcs ||= ""){
		fhem("attr LCSSERVICE devStateIcon .*:general_an\@green");
		fhem("setreading LCSSERVICE processid $lcs");
	}
	else {
		fhem("attr LCSSERVICE devStateIcon .*:general_aus\@red");
		fhem("setreading LCSSERVICE processid - - -");
		fhem("set LCSSERVICE off");
	}
}

+*00:05:00 {
	my $alexa=`pgrep alexa`;
	my $lcs=`ps ax | grep ld382a.py | grep -v grep | cut -d" " -f1`;
	if ($alexa ||= ""){
		fhem("attr ALEXASERVICE devStateIcon .*:general_an\@green");
		fhem("setreading ALEXASERVICE processid $alexa");
	}
	else {
		fhem("attr ALEXASERVICE devStateIcon .*:general_aus\@red");
		fhem("setreading ALEXASERVICE processid - - -");
		fhem("set ALEXASERVICE off");
	}
	if ($lcs ||= ""){
		fhem("attr LCSSERVICE devStateIcon .*:general_an\@green");
		fhem("setreading LCSSERVICE processid $lcs");
	}
	else {
		fhem("attr LCSSERVICE devStateIcon .*:general_aus\@red");
		fhem("setreading LCSSERVICE processid - - -");
		fhem("set LCSSERVICE off");
	}
}

