{
	fhem 'set Zeit '.strftime('%H:%M Uhr | %d-%m-%Y', localtime);
	my $timecalc=int(strftime('%H%M', localtime));
	my $check = 0;
	my $start1=Value("ZEIT1START");
	$start1=~s/://g;
	my $stop1=Value("ZEIT1STOP");
	$stop1=~s/://g;
	my $start2=Value("ZEIT2START");
	$start2=~s/://g;
	my $stop2=Value("ZEIT2STOP");
	$stop2=~s/://g;
	my $start3=Value("ZEIT3START");
	$start3=~s/://g;
	my $stop3=Value("ZEIT3STOP");
	$stop3=~s/://g;
	if (($timecalc >= $start1) && ($timecalc <= $stop1)){
		if (Value("OVERRIDE") ne "Ein"){
			fhem "set AUTOMATIKFENSTER Morgen";
		}
		else {
			fhem "set AUTOMATIKFENSTER Abend";
		}
		$check = 1;
	}
	if (($timecalc >= $start2) && ($timecalc <= $stop2)){
		fhem "set AUTOMATIKFENSTER Abend";
		$check = 1;
	}
	if (($timecalc >= $start3) && ($timecalc <= $stop3)){
		if (Value("OVERRIDE") ne "Ein"){
			fhem "set AUTOMATIKFENSTER Nacht";
		}
		else {
			fhem "set AUTOMATIKFENSTER Abend";
		}
		$check = 1;
	}
	if ($check ne 1) {
		if (Value("OVERRIDE") ne "Ein"){
			fhem "set AUTOMATIKFENSTER - - -";
		}
		else {
			fhem "set AUTOMATIKFENSTER Abend";
		}
	}
	fhem 'set LCD clear';
	fhem 'set LCD writeXY 3,0,10 '.strftime('%H:%M  Uhr', localtime);
	fhem 'set LCD writeXY 1,1,14 '.strftime('%a, %d-%m-%Y', localtime);
}

+*00:00:59 {
	fhem 'set Zeit '.strftime('%H:%M Uhr | %d-%m-%Y', localtime);
	my $timecalc=int(strftime('%H%M', localtime));
	my $check = 0;
	my $start1=Value("ZEIT1START");
	$start1=~s/://g;
	my $stop1=Value("ZEIT1STOP");
	$stop1=~s/://g;
