{
  if ( ( Value("wakeUpStatus") eq "Wochentage" && !$we ) || ( Value("wakeUpStatus") eq "Wochenende" && $we ) ||
  ( Value("wakeUpStatus"q "Taeglich" ) || ( Value("wakeUpStatus") eq "Einmalig" ) ) {
     fhem ('set LD382A 50;');
     fhem ('set LEDSWZ 50;');
     fhem ('set Alarm blink 5 1;');
    if ( Value("wakeUpStatus") eq "Einmalig" ) { fhem ("set wakeUpStatus Aus") }
  }
}

*07:00 {
  if ( ( Value("wakeUpStatus") eq "Wochentage" && !$we ) || ( Value("wakeUpStatus") eq "Wochenende" && $we ) ||
  ( Value("wakeUpStatus") eq "Taeglich" ) || ( Value("wakeUpStatus") eq "Einmalig" ) ) {
     fhem ('set LD382A 50;');
     fhem ('set LEDSWZ 50;');
     fhem ('set Alarm blink 5 1;');
    if ( Value("wakeUpStatus") eq "Einmalig" ) { fhem ("set wakeUpStatus Aus") }
