KalenderAbfall:.*|sysstat:.* {
	my $ICON;
	my $LINE1 = ReadingsVal("KalenderAbfall","now_text","Keine Abholung");
	$LINE1 =~ s/^\s+|\s+$//g;
	$LINE1 = " Heute: ".$LINE1;
	if (($LINE1 =~ "Keine Abholung") && (ReadingsVal("KalenderAbfall","next_days","0") eq "1")){
		$LINE1 = ReadingsVal("KalenderAbfall","next_text","Keine Abholung");
		$LINE1 =~ s/^\s+|\s+$//g;
		$LINE1 = " Morgen: ".$LINE1;
	}
	if ((ReadingsVal("AUTOMATIKFENSTER", "state", "") ne "Morgen") && (ReadingsVal("KalenderAbfall","next_days","0") eq "1")){
		$LINE1 = ReadingsVal("KalenderAbfall","next_text","Keine Abholung");
		$LINE1 =~ s/^\s+|\s+$//g;
		$LINE1 = " Morgen: ".$LINE1;
	}
	if ($LINE1 =~ "Altpapier"){
		$ICON = "i12059";
	}
	elsif ($LINE1 =~ "Gelbe"){
		$ICON = "i12212";
	}
	elsif ($LINE1 =~ "Restabfall"){
		$ICON = "i11979";
	}
	else {
		$ICON = "i9398";
	}
	$LINE1 =~ s/EAD //g;
	fhem ("setreading INFO Line1 ".$LINE1);
	fhem ("setreading INFO Line1_icon ".$ICON);
}

ACTIONBACK:.* {
	if (($EVENT eq "back") && (Value("Automatik") eq "Ein") && (Value("GLOBALANWESENHEIT") eq "present")){
		fhem ("set AUTOCOLORUPDATE_CHK active;");
		if ((Value("AUTOMATIKFENSTER") eq "Abend") && (ReadingsVal("KODI", "playStatus", "") ne "playing" )) {
			fhem ("set LEDSBZ RGB FFFFFF;");
			fhem ("set LEDSWZ RGB FFFFFF;");
			fhem ('set LD382A 100;');
		}
		if (Value("Automatik") eq "Ein") {
			fhem ("set KODIMASTER Ein;");
			fhem ("set NAS01 on;");
			fhem ("set NAS02 on;");
		}
		fhem ("sleep 1; set ACTIONBACK ready;");
	}
	else {
		fhem ("sleep 1; set ACTIONBACK ready;");
	}
}

ACTIONGONE:.* {
	if (($EVENT eq "gone") && (Value("Automatik") eq "Ein")){

		fhem ("set AUTOCOLORUPDATE_CHK inactive;");
		if (ReadingsVal("KODI","playStatus",undef) ne "stopped" ){
			fhem ("set KODI stop;");
		}
		if (ReadingsVal("KODIMASTER","state",undef) ne "off" ){
			fhem ("set KODIMASTER off;");
		}
		if ((Value("LED0") ne "Aus" ) || (Value("LED1") ne "Aus" ) || (Value("LED3") ne "Aus" )) {
			fhem ("set LEDSWZ 0;");
		}
		if (Value("LED4") ne "off" ) {
			fhem ("set LD382A 0;");
		}
		if (Value("LED2") ne "off" ) {
			fhem ("set LEDSBZ 0;");
		}
		if (Value("Ambilight") ne "Aus" ) {
			fhem ("set HyperionSwitch off; set Ambilight off;");
		}
		if (Value("Kaffeemaschine") ne "Aus" ) {
			fhem ("set Kaffeemaschine off;");
		}
		if (Value("Drucker") ne "Aus" ) {
			fhem ("set Drucker off;");
		}
		if (Value("LG_REMOTE") ne "Aus" ) {
			fhem ("set LG_REMOTE off;");
		}
		if (Value("YAMAHA_REMOTE") ne "Aus" ) {
			fhem ("set YAMAHA_REMOTE off;");
		}
		if (ReadingsVal("NAS01","isRunning","") eq "true" ) {
			fhem ("set NAS01 off;");
		}
		if (ReadingsVal("NAS02","isRunning","") eq "true" ) {
			fhem ("set NAS02 off;");
		}
		#fhem("set WAMHillesheim send Gesamtabschaltung erfolgt!");
		fhem ("sleep 1; set ACTIONGONE ready;");
	}
}

VERKEHR:.* {
         if (ReadingsVal("VERKEHR", "state", "") eq "on" ){
            fhem('set Alarm on; sleep 0.15; set Alarm off; sleep 0.25; set Alarm on; sleep 0.15; set Alarm off;');
         }
}

alexa:fhemIntent:.* {
 if (ReadingsVal("alexa", "fhemIntent", "") eq "BeleuchtungAnpassen"){
   if (ReadingsVal("KODI", "playStatus", "") eq "playing"){
         if (ReadingsVal("KODI", "type", "") eq "movie"){
             fhem ('define SetPlaybackFilm at +00:00:01 set SZENEN FILM');
         }
         if (ReadingsVal("KODI", "type", "") eq "musicvideo"){
             fhem ('define SetPlaybackMusik at +00:00:01 set SZENEN MUSIKVIDEO');
         }
         if (ReadingsVal("KODI", "type", "") eq "song"){
             fhem ('define SetPlaybackMusik at +00:00:01 set SZENEN MUSIK');
         }
         if (ReadingsVal("KODI", "type", "") eq "episode"){
             fhem ('define SetPlaybackSerie at +00:00:01 set SZENEN SERIE');
         }
         if (ReadingsVal("KODI", "type", "") eq "unknown"){
             fhem ('define SetPlaybackSerie at +00:00:01 set SZENEN DIVERSES');
         }
         if (ReadingsVal("KODI", "type", "") eq "channel"){
             fhem ('define SetPlaybackSerie at +00:00:01 set SZENEN TV');
         }
    }
	if (ReadingsVal("KODI", "playStatus", "") eq "stopped" ){
		fhem('"set SZENEN STOP');
	}
	if (ReadingsVal("KODI", "playStatus", "") eq "paused" ){
		fhem('"set SZENEN PAUSE');
	}
 }
 if (ReadingsVal("alexa", "fhemIntent", "") eq "gutenMorgen"){
     fhem("set LEDSWZ 25;");
     fhem("set LED3 HSV 0,0,100 600;");
 }
 if (ReadingsVal("alexa", "fhemIntent", "") eq "guteNacht"){
     fhem("set Beleuchtung Aus;")
 }
 if (ReadingsVal("alexa", "fhemIntent", "") eq "AlexaNeustart"){
     fhem("set ALEXANEUSTART Jetzt;")
 }
}

ALEXANEUSTART:.* {
	if (Value("ALEXANEUSTART") eq "Jetzt") {
		fhem ("set ALEXAPOWER Aus;");
		fhem ('define RestartAlexa at +00:00:05 {fhem ("set ALEXANEUSTART Ein")};');
	}
	if (Value("ALEXANEUSTART") eq "Ein") {
		fhem ("set ALEXAPOWER Ein;");
	}
}

ALEXASERVICE:.* {
	my $running=`pgrep alexa`;
	if (Value("ALEXASERVICE") eq "Ein"){
		if ($running ||= ""){
			fhem("attr ALEXASERVICE devStateIcon .*:general_an\@green");
			fhem("setreading ALEXASERVICE processid $running");
		}
		else {
			fhem("attr ALEXASERVICE devStateIcon .*:general_an\@green");
			system("sudo service alexa start");
			$running=`pgrep alexa`;
			if ($running ||= ""){
				fhem("attr ALEXASERVICE devStateIcon .*:general_an\@green");
				fhem("setreading ALEXASERVICE processid $running");
			}
			else {
				fhem("attr ALEXASERVICE devStateIcon .*:general_aus\@red");
				fhem("setreading ALEXASERVICE processid - - -");
			}
		}
	}
	if (Value("ALEXASERVICE") eq "Aus"){
		system("sudo service alexa stop");
		system("sudo service alexa stop");
		fhem("setreading ALEXASERVICE processid - - -");
		fhem("attr ALEXASERVICE devStateIcon .*:general_aus\@red");
	}
}

