AUTOSZENE:.* {
	if ((ReadingsVal("KODI", "playStatus", "") eq "playing") && ($EVENT ne "off")){
         if (ReadingsVal("KODI", "type", "") eq "movie"){
             fhem ('set SZENEN FILM');
         }
         if (ReadingsVal("KODI", "type", "") eq "musicvideo"){
             fhem ('set SZENEN MUSIKVIDEO');
         }
         if (ReadingsVal("KODI", "type", "") eq "song"){
             fhem ('set SZENEN MUSIK');
         }
         if (ReadingsVal("KODI", "type", "") eq "episode"){
             fhem ('set SZENEN SERIE');
         }
         if (ReadingsVal("KODI", "type", "") eq "unknown"){
		 	my $media = ReadingsVal("KODI", "currentMedia", "");
			my $netflix = "http://localhost";
			my $amazon = "cloudfront";
		 	if (index($media, $netflix) != -1){
				fhem ('set SZENEN NETFLIX');
			}
			elsif (index($media, $netflix) != -1){
				fhem ('set SZENEN AMAZON');

			}
			else {
				fhem ('set SZENEN DIVERSES');
			}

         }
         if (ReadingsVal("KODI", "type", "") eq "channel"){
             fhem ('set SZENEN TV');
         }
    }
	fhem ('sleep 2; set AUTOSZENE off');
}

TSL2561:broadband.* {
my $bright=ReadingsVal("LD382A","state","");
if ($bright eq "on") {
   $bright = 100;
}
if ($bright eq "off") {
   $bright = 0;
}
         if ((ReadingsVal("TSL2561","broadband",'') < 50) && (ReadingsVal("TSL2561","ir",'') < 20)) {
             fhem('set Belichtung dunkel');
         }
         if ((ReadingsVal("TSL2561","broadband",'') > 50) && (ReadingsVal("TSL2561","ir",'') > 20)) {
            if ($bright < 20) {
                fhem("set Belichtung hell");
            }
         }
}

ColorUpdate:push {
	my $color = ReadingsVal("LED2", "hue", undef);
	my $newcolor = $color+30;
	if (((Value("Tageslicht") eq "dunkel") || (Value("Belichtung") eq "dunkel")) && (Value("Automatik") eq "Ein") && ((ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Morgen")||(ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Abend")||(ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Nacht"))){
		if ((ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Morgen") || (ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Abend")){
			if ($newcolor > 360) {
    			$newcolor = 0;
    			fhem "set LED2 HSV $newcolor,100,100 1";
    		}
			else {
    			fhem "set LED2 HSV $newcolor,100,100 1";
    		}
		}
		if (ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Nacht"){
			if ($newcolor > 360) {
    			$newcolor = 0;
    			fhem "set LED2 HSV $newcolor,100,25 1";
    		}
			else {
    			fhem "set LED2 HSV $newcolor,100,25 1";
    		}
		}
		fhem ('define GETLEDValues at +00:00:01 {getLEDValues("LED2","LEDSBZ")};');
	}
	else {
		if (ReadingsVal("LED2","state","") ne "off"){
			fhem "set LEDSBZ off";
			fhem ('define GETLEDValues at +00:00:01 {getLEDValues("LED2","LEDSBZ")};');
		}
	}
}

ColorUpdateWZ:push {
my $color = ReadingsVal("LED0", "hue", undef);
my $newcolor = $color+30;
if ($newcolor > 360) {
    $newcolor = 0;
    fhem "set LED0,LED1 HSV $newcolor,0,100 1; set LED4 HSV $newcolor,0,50 1";
    }
else {
    fhem "set LED0,LED1 HSV $newcolor,100,100 1; set LED4 HSV $newcolor,100,50 1";
    }
}

ELRO1:.* {
	my $brightness=ReadingsVal("LED3", "brightness", "0");
	if (Value("ELRO1") eq 'Ein') {
		if (($brightness == 100) && (ReadingsVal("LD382A", "pilight", "") eq "no")){
			fhem("setreading LD382A pilight yes");
			fhem("set LD382A 0");
		}
		elsif (($brightness >= 75 && $brightness < 100) && (ReadingsVal("LD382A", "pilight", "") eq "no")){
			fhem("setreading LD382A pilight yes");
			fhem("set LD382A 100");
		}
		elsif (($brightness >= 50 && $brightness < 75) && (ReadingsVal("LD382A", "pilight", "") eq "no")){
			fhem("setreading LD382A pilight yes");
			fhem("set LD382A 75");
		}
		elsif (($brightness >= 25 && $brightness < 50) && (ReadingsVal("LD382A", "pilight", "") eq "no")){
			fhem("setreading LD382A pilight yes");
			fhem("set LD382A 50");
		}
		elsif (($brightness >= 0 && $brightness < 25) && (ReadingsVal("LD382A", "pilight", "") eq "no")){
			fhem("setreading LD382A pilight yes");
			fhem("set LD382A 25");
		}
	}
	else {
		if (($brightness > 75 && $brightness <= 100) && (ReadingsVal("LD382A", "pilight", "") eq "no")){
			fhem("setreading LD382A pilight yes");
			fhem("set LD382A 75");
		}
		elsif (($brightness > 50 && $brightness <= 75) && (ReadingsVal("LD382A", "pilight", "") eq "no")){
			fhem("setreading LD382A pilight yes");
			fhem("set LD382A 50");
		}
		elsif (($brightness > 25 && $brightness <= 50) && (ReadingsVal("LD382A", "pilight", "") eq "no")){
			fhem("setreading LD382A pilight yes");
			fhem("set LD382A 25");
		}
		elsif (($brightness > 0 && $brightness <= 25) && (ReadingsVal("LD382A", "pilight", "") eq "no")){
			fhem("setreading LD382A pilight yes");
			fhem("set LD382A 0");
		}
		elsif (($brightness == 0) && (ReadingsVal("LD382A", "pilight", "") eq "no")){
			fhem("setreading LD382A pilight yes");
			fhem("set LD382A 100");
		}
	}
	fhem ('define ld382apilight at +00:00:02 {fhem("setreading LD382A pilight no")};');
}

