GEWITTER:Ein.*|UNWETTER:WarnCount:.* {
	if (ReadingsVal("LED0","exec","") eq "no"){
		if ((Value("GEWITTER") eq "Ein") or (ReadingsVal("Wetter","condition","") =~ "Gewitter")){
			fhem ("setreading LED0 exec yes");
			Ligthning("LED0");
			my $TEXT0=ReadingsVal("Wetter","condition","");
			if ($TEXT0 ne ""){
				#fhem "set LAMETRIC msg 'i419' ' Alarm: $TEXT0' 'alarms:alarm13';";
				fhem "set LAMETRIC msg 'i419' ' Alarm: $TEXT0';";
			}
		}
		my $TEXT1=ReadingsVal("UNWETTER","Warn_0_ShortText","");
		my $TEXT2=ReadingsVal("UNWETTER","Warn_1_ShortText","");
		if ($TEXT1 ne ""){
			#fhem "set LAMETRIC msg 'i419' ' Alarm: $TEXT1' 'alarms:alarm13';";
			fhem "set LAMETRIC msg 'i419' ' Alarm: $TEXT1';";
		}
		if ($TEXT2 ne ""){
			#fhem "set LAMETRIC msg 'i419' ' Alarm: $TEXT2' 'alarms:alarm13';";
			fhem "set LAMETRIC msg 'i419' ' Alarm: $TEXT2';";
		}
	}
	fhem ('define ResetGewitter at +00:00:10 setreading LED0 exec no;; set GEWITTER off;');
}

