INFO:.* {
	my @a = split(/\ /,$EVENT);
	if (($a[0] eq "Line1") || ($a[0] eq "Line2") || ($a[0] eq "Line3") || ($a[0] eq "Line4") || ($a[0] eq "Line5")) {
		if (length($a[1] ||= '')) {
			fhem ("setreading INFO ".$EVENT);
			fhem ("setreading INFO state done");
		}
		else {
			fhem ("setreading INFO ".$a[0]." none");
			fhem ("setreading INFO state done");
		}
	}
	if (($a[0] eq "Line1_icon") || ($a[0] eq "Line2_icon") || ($a[0] eq "Line3_icon") || ($a[0] eq "Line4_icon") || ($a[0] eq "Line5_icon")) {
		if (length($a[1] ||= '')) {
			fhem ("setreading INFO ".$a[0]." ".$a[1]);
			fhem ("setreading INFO state done");
		}
		else {
			fhem ("setreading INFO ".$a[0]." none");
			fhem ("setreading INFO state done");
		}
	}
}

