KLIMA_REMOTE {
	if (ReadingsVal("KLIMA_REMOTE","last_state","") eq "on") {
		if ($EVENT eq "Aus"){
			system("irsend SEND_ONCE KLIMA POWER");
			fhem "setreading KLIMA_REMOTE last_state off";
			fhem "setstate KLIMA_REMOTE Aus";
		}
		if ($EVENT eq "func TROCKNEN"){
			if (ReadingsVal("KLIMA_REMOTE","func","") eq "cool"){
				system("irsend SEND_ONCE KLIMA FUNC");
				fhem "setreading KLIMA_REMOTE func dry";
				fhem "setreading KLIMA_REMOTE last_state on";
				fhem "setstate KLIMA_REMOTE Ein";
			}
		}
		if ($EVENT eq "func KUEHLEN"){
			if (ReadingsVal("KLIMA_REMOTE","func","") eq "dry"){
				system("irsend SEND_ONCE KLIMA FUNC");
				fhem "setreading KLIMA_REMOTE func cool";
				fhem "setreading KLIMA_REMOTE last_state on";
				fhem "setstate KLIMA_REMOTE Ein";
			}
		}
		if (($EVENT eq "speed STUFE1") && (ReadingsVal("KLIMA_REMOTE","func","") eq "cool")){
			if (ReadingsVal("KLIMA_REMOTE","speed","") ne "1"){
				system("irsend SEND_ONCE KLIMA LOW");
				fhem "setreading KLIMA_REMOTE speed 1";
				fhem "setreading KLIMA_REMOTE last_state on";
				fhem "setstate KLIMA_REMOTE Ein";
			}
		}
		if (($EVENT eq "speed STUFE2") && (ReadingsVal("KLIMA_REMOTE","func","") eq "cool")){
			if (ReadingsVal("KLIMA_REMOTE","speed","") ne "2"){
				system("irsend SEND_ONCE KLIMA MID");
				fhem "setreading KLIMA_REMOTE speed 2";
				fhem "setreading KLIMA_REMOTE last_state on";
				fhem "setstate KLIMA_REMOTE Ein";
			}
		}
		if (($EVENT eq "speed STUFE3") && (ReadingsVal("KLIMA_REMOTE","func","") eq "cool")){
			if (ReadingsVal("KLIMA_REMOTE","speed","") ne "3"){
				system("irsend SEND_ONCE KLIMA HI");
				fhem "setreading KLIMA_REMOTE speed 3";
				fhem "setreading KLIMA_REMOTE last_state on";
				fhem "setstate KLIMA_REMOTE Ein";
			}
		}
		if (($EVENT eq "speed AUTO") && (ReadingsVal("KLIMA_REMOTE","func","") eq "cool")){
			if (ReadingsVal("KLIMA_REMOTE","speed","") ne "auto"){
				system("irsend SEND_ONCE KLIMA AUTO");
				fhem "setreading KLIMA_REMOTE speed auto";
				fhem "setreading KLIMA_REMOTE last_state on";
				fhem "setstate KLIMA_REMOTE Ein";
			}
		}
		if (($EVENT eq "daynight NACHT") && (ReadingsVal("KLIMA_REMOTE","mode","") eq "day")){
			if (ReadingsVal("KLIMA_REMOTE","mode","") ne "night"){
				system("irsend SEND_ONCE KLIMA SLEEP");
				fhem "setreading KLIMA_REMOTE mode night";
				fhem "setreading KLIMA_REMOTE last_state on";
				fhem "setstate KLIMA_REMOTE Ein";
			}
		}
		if (($EVENT eq "daynight TAG") && (ReadingsVal("KLIMA_REMOTE","mode","") eq "night")){
			if (ReadingsVal("KLIMA_REMOTE","mode","") ne "day"){
				system("irsend SEND_ONCE KLIMA SLEEP");
				fhem "setreading KLIMA_REMOTE mode day";
				fhem "setreading KLIMA_REMOTE last_state on";
				fhem "setstate KLIMA_REMOTE Ein";
			}
		}
	}
	if (Value("KLIMA_REMOTE") eq "Ein") {
		if (ReadingsVal("KLIMA_REMOTE","last_state","") eq "off"){
			system("irsend SEND_ONCE KLIMA POWER");
			fhem "setreading KLIMA_REMOTE last_state on";
			fhem "setstate KLIMA_REMOTE Ein";
			# start set cool temp
			my $TempDiff = 0;
			my $CurrentTemp = ReadingsVal('KLIMA_REMOTE','temp','');
			my $DestTemp = ReadingsVal('TEMPMAX','state','');
			if ($DestTemp > $CurrentTemp){
				$TempDiff = $DestTemp - $CurrentTemp;
				if ((ReadingsVal("KLIMA_REMOTE","last_state","") eq "on") && (ReadingsVal("KLIMA_REMOTE","func","") eq "cool")) {
					foreach my $i (0..$TempDiff) {
						fhem "set LCD text $CurrentTemp $DestTemp $TempDiff $i";
						system("irsend SEND_ONCE KLIMA UP");
						$i++;
					}
					fhem "setreading KLIMA_REMOTE temp $DestTemp";
					system("irsend SEND_ONCE KLIMA AUTO");
					fhem "setreading KLIMA_REMOTE speed auto";
				}
			}
			if ($DestTemp < $CurrentTemp){
				$TempDiff = $CurrentTemp - $DestTemp;
				if ((ReadingsVal("KLIMA_REMOTE","last_state","") eq "on") && (ReadingsVal("KLIMA_REMOTE","func","") eq "cool")) {
					foreach my $i (0..$TempDiff) {
						fhem "set LCD text $DestTemp $CurrentTemp $TempDiff $i";
						system("irsend SEND_ONCE KLIMA DOWN");
						$i++;
					}
					fhem "setreading KLIMA_REMOTE temp $DestTemp";
					system("irsend SEND_ONCE KLIMA AUTO");
					fhem "setreading KLIMA_REMOTE speed auto";
				}
			}
			# end set cool temp
		}
	}
}

