}

LCSSERVICE:.* {
	my $running=`ps ax | grep ld382a.py | grep -v grep | cut -d" " -f1`;
	if (Value("LCSSERVICE") eq "Ein"){
		if ($running ||= ""){
			fhem("attr LCSSERVICE devStateIcon .*:general_an\@green");
			fhem("setreading LCSSERVICE processid $running");
		}
		else {
			fhem("attr LCSSERVICE devStateIcon .*:general_an\@green");
			system("sudo service lcs start");
			$running=`ps ax | grep ld382a.py | grep -v grep | cut -d" " -f1`;
			if ($running ||= ""){
				fhem("attr LCSSERVICE devStateIcon .*:general_an\@green");
				fhem("setreading LCSSERVICE processid $running");
			}
			else {
				fhem("attr LCSSERVICE devStateIcon .*:general_aus\@red");
				fhem("setreading LCSSERVICE processid - - -");
			}
		}
	}
	if (Value("LCSSERVICE") eq "Aus"){
		system("sudo service lcs stop");
		fhem("setreading LCSSERVICE processid - - -");
		fhem("attr LCSSERVICE devStateIcon .*:general_aus\@red");
	}
}

LD382A:.* {
  if (ReadingsVal("LD382A","exec","") ne "yes"){
	fhem "setreading LD382A exec yes";
	my $dim;
	my $state=ReadingsVal("LD382A","state","");
	my @a = split(/\ /,$state);
	if (length($a[1] ||= '')) {
		$dim = "$a[1]";
	}
	else {
		$dim = "$a[0]";
	}
	my $hueStart = ReadingsVal("LED3","hue","");
	my $satStart = ReadingsVal("LED3","saturation","");
	my $intStart = ReadingsVal("LED3","brightness","");
	if ($dim eq "NaN") {
		$dim = $intStart;
	}
	if ($dim eq "off") {
		$dim = "0";
	}
	if ($dim eq "on") {
		$dim = "100";
		fhem "setstate LD382A on";
	}
	if (($a[0] eq "brightness") && ($a[1] eq "")){
		$dim = "0";
		fhem "setstate LD382A off";
	}
	lcs("T,$hueStart,$satStart,$intStart,0,0,$dim,0.5");
	getLD382aValues("LED3",1);
	fhem ('define GETLEDValuesLED3 at +00:00:01 {getLEDValues("LED3","LD382A")};');
  }
}

LEDSBZ:.* {
  if (ReadingsVal("LEDSBZ","exec","") ne "yes"){
	fhem "setreading LEDSWZ exec yes";
	my $dim = "0";
	my $color = "000000";
	my $rgb=ReadingsVal("LED2","RGB","");
	my $state = Value("LEDSBZ");
	my @a = split(/\ /,$state);

	if ((length($a[1] ||= '')) && ($a[0] ne "brightness")) {
		$color = "$a[1]";
		if (ReadingsVal("LED2","hue","") gt 0){
			my $hue=ReadingsVal("LED2","hue","");
			fhem "set LED2 RGB $color";
			fhem "set LED2 hue $hue";
		}
		else {
			fhem "set LED2 RGB $color";
		}
		fhem "setstate LEDSBZ on;";
		fhem ('define GETLEDValues at +00:00:01 {getLEDValues("LED2","LEDSBZ")};');
	}
	else {
		if ($a[0] eq "on"){
			$dim="100";
		}
		elsif ($a[0] eq "off"){
			$dim="0";
		}
		else {
			$dim="$a[1]";
			if ($dim eq ""){
				$dim="0";
			}
		}
		fhem("set LED2 dim $dim 0.5");
		fhem ('define GETLEDValuesLED2 at +00:00:01 {getLEDValues("LED2","LEDSBZ")};');
	}
  }
}

LEDSWZ:.* {
  if (ReadingsVal("LEDSWZ","exec","") ne "yes"){
	fhem "setreading LEDSWZ exec yes";
	my $dim = "0";
	my $dim2 = "0";
	my $color = "000000";
	my $rgb=ReadingsVal("LED0","RGB","");
	my $state = Value("LEDSWZ");
	my @a = split(/\ /,$state);

	if ((length($a[1] ||= '')) && ($a[0] ne "brightness")) {
		$color = "$a[1]";
		if (ReadingsVal("LED0","hue","") gt 0){
			my $hue=ReadingsVal("LED0","hue","");
			fhem "set LED0,LED1 RGB $color";
			fhem "set LED0,LED1 hue $hue";
		}
		else {
			fhem "set LED0,LED1 RGB $color";
		}
		my $huemaster=ReadingsVal("LED0","hue","");
		my $saturationmaster=ReadingsVal("LED0","saturation","");
		my $brightnessmaster=ReadingsVal("LED0","brightness","");
		my $brightnessmaster2=sprintf"%.0f", $brightnessmaster/2,0;
		fhem "set LED4 HSV $huemaster,$saturationmaster,$brightnessmaster2;";
		fhem "setstate LEDSWZ on;";
		#getLEDValues("LED0","LEDSWZ");
		fhem ('define GETLEDValues at +00:00:01 {getLEDValues("LED0","LEDSWZ")};');
	}
	else {
		if (($a[0] ne "brightness") && ($a[0] ne "on") && ($a[0] ne "off")) {
			$dim="$a[0]";
			$dim2=sprintf"%.0f", $dim/2,0;
		}
		elsif ($a[0] eq "on"){
			$dim="100";
			$dim2=sprintf"%.0f", $dim/2,0;
		}
		elsif ($a[0] eq "off"){
			$dim="0";
			$dim2="0";
		}
		if ($a[0] eq "brightness") {
			$dim="$a[1]";
			$dim2=sprintf"%.0f", $dim/2,0;
		}
		fhem("set LED0,LED1 dim $dim 0.5");
		fhem("set LED4 dim $dim2 0.5");
	}
	fhem ('define GETLEDValuesLED0 at +00:00:02 {getLEDValues("LED0","LEDSWZ")};');
  }
}

LG_REMOTE {
   if ("$EVENT" eq "KODI"){
      $EVENT = "HDMI1";
   }
	fhem 'set LCD clear';
	fhem 'set LCD writeXY 0,0,10 FERNSEHER:';
	fhem 'set LCD writeXY 0,1,'.length($EVENT).' '.$EVENT;
   if (Value("LG_REMOTE") eq "Ein"){
       system("irsend SEND_ONCE LGHARMONY on");
	   fhem("setstate LG_REMOTE on");
   }
   if (Value("LG_REMOTE") ne "Aus"){
       system("irsend SEND_ONCE YAMAHA on");
	   fhem("setstate LG_REMOTE Ein");
	   my @input = split(/\ /,$EVENT);
	   my $sendkey;
	   if (length($input[1] ||= '')){
	   		$sendkey = $input[1];
	   }
	   else {
	   	   	$sendkey = $input[0];
	   }
	   if (($sendkey ne "on") && ($sendkey ne "off") && ($sendkey ne "Ein") && ($sendkey ne "Aus")) {
			fhem("setreading LG_REMOTE input $sendkey");
	   }
       system("sleep 1;irsend SEND_ONCE LGHARMONY $sendkey");
   }
   if (Value("LG_REMOTE") eq "Aus"){
       system("irsend SEND_ONCE LGHARMONY off");
       fhem("setstate LG_REMOTE Aus");
   }
}

LUEFTER_REMOTE {
	fhem 'set LCD clear';
	fhem 'set LCD writeXY 0,0,8 LUEFTER:';
	fhem 'set LCD writeXY 0,1,'.length($EVENT).' '.$EVENT;
	my $speed = 1;
	if (ReadingsVal("LUEFTER_REMOTE","last_state","") eq "on") {
		if (ReadingsVal("LUEFTER_REMOTE","state","") eq "NACHT"){
			if (ReadingsVal("LUEFTER_REMOTE","mode","") eq "day"){
				system("irsend SEND_ONCE FAN MODE");
				sleep 0.5; system("irsend SEND_ONCE FAN MODE");
				fhem "setreading LUEFTER_REMOTE mode night";
			}
			fhem "setreading LUEFTER_REMOTE last_state on";
			fhem "setstate LUEFTER_REMOTE Ein";
		}
		if (ReadingsVal("LUEFTER_REMOTE","state","") eq "TAG"){
			if (ReadingsVal("LUEFTER_REMOTE","mode","") eq "night"){
				system("irsend SEND_ONCE FAN MODE");
				fhem "setreading LUEFTER_REMOTE mode day";
			}
			fhem "setreading LUEFTER_REMOTE last_state on";
			fhem "setstate LUEFTER_REMOTE Ein";
		}
		if (ReadingsVal("LUEFTER_REMOTE","state","") eq "DREHEN"){
			if (ReadingsVal("LUEFTER_REMOTE","osc","") eq "no"){
				system("irsend SEND_ONCE FAN osc");
				fhem "setreading LUEFTER_REMOTE osc yes";
			}
			else {
				fhem "setreading LUEFTER_REMOTE osc yes";
			}
			fhem "setreading LUEFTER_REMOTE last_state on";
			fhem "setstate LUEFTER_REMOTE Ein";
		}
		if (ReadingsVal("LUEFTER_REMOTE","state","") eq "STOP"){
			if (ReadingsVal("LUEFTER_REMOTE","osc","") eq "yes"){
				system("irsend SEND_ONCE FAN osc");
				fhem "setreading LUEFTER_REMOTE osc no";
			}
			else {
				fhem "setreading LUEFTER_REMOTE osc no";
			}
			fhem "setreading LUEFTER_REMOTE last_state on";
			fhem "setstate LUEFTER_REMOTE Ein";
		}
		if (ReadingsVal("LUEFTER_REMOTE","state","") eq "STUFE1"){
			if (ReadingsVal("LUEFTER_REMOTE","speed","") eq "1"){
				$speed = 1;
			}
			if (ReadingsVal("LUEFTER_REMOTE","speed","") eq "2"){
				system("irsend SEND_ONCE FAN on");
				system("irsend SEND_ONCE FAN on");
				$speed = 1;
			}
			if (ReadingsVal("LUEFTER_REMOTE","speed","") eq "3"){
				system("irsend SEND_ONCE FAN on");
				$speed = 1;
			}
			fhem "setreading LUEFTER_REMOTE speed $speed";
			fhem "setreading LUEFTER_REMOTE last_state on";
			fhem "setstate LUEFTER_REMOTE Ein";
		}
		if (ReadingsVal("LUEFTER_REMOTE","state","") eq "STUFE2"){
			if (ReadingsVal("LUEFTER_REMOTE","speed","") eq "1"){
				system("irsend SEND_ONCE FAN on");
				$speed = 2;
			}
			if (ReadingsVal("LUEFTER_REMOTE","speed","") eq "2"){
				$speed = 2;
			}
			if (ReadingsVal("LUEFTER_REMOTE","speed","") eq "3"){
				system("irsend SEND_ONCE FAN on");
				system("irsend SEND_ONCE FAN on");
				$speed = 2;
			}
			fhem "setreading LUEFTER_REMOTE speed $speed";
			fhem "setreading LUEFTER_REMOTE last_state on";
			fhem "setstate LUEFTER_REMOTE Ein";
		}
		if (ReadingsVal("LUEFTER_REMOTE","state","") eq "STUFE3"){
			if (ReadingsVal("LUEFTER_REMOTE","speed","") eq "1"){
				system("irsend SEND_ONCE FAN on");
				system("irsend SEND_ONCE FAN on");
				$speed = 3;
			}
			if (ReadingsVal("LUEFTER_REMOTE","speed","") eq "2"){
				system("irsend SEND_ONCE FAN on");
				$speed = 3;
			}
			if (ReadingsVal("LUEFTER_REMOTE","speed","") eq "3"){
				$speed = 3;
			}
			fhem "setreading LUEFTER_REMOTE speed $speed";
			fhem "setreading LUEFTER_REMOTE last_state on";
			fhem "setstate LUEFTER_REMOTE Ein";
		}
	}
	else {
		#fhem "setreading LUEFTER_REMOTE last_state off";
		#fhem "setstate LUEFTER_REMOTE Aus";
	}

	if (Value("LUEFTER_REMOTE") eq "Ein") {
		if (ReadingsVal("LUEFTER_REMOTE","last_state","") eq "off"){
			system("irsend SEND_ONCE FAN on");
			fhem "setreading LUEFTER_REMOTE last_state on";
		}
	}
	if (Value("LUEFTER_REMOTE") eq "Aus") {
		if (ReadingsVal("LUEFTER_REMOTE","last_state","") eq "on"){
			system("irsend SEND_ONCE FAN off");
			fhem "setreading LUEFTER_REMOTE last_state off";
			fhem "setreading LUEFTER_REMOTE mode day";
		}
	}
}

