     system("irsend SEND_ONCE ledstripe red");
  }
  if (Value("RGB") eq "RGB 00ff00"){
     system("irsend SEND_ONCE ledstripe on");
     system("irsend SEND_ONCE ledstripe green");
  }
  if (Value("RGB") eq "RGB 0000ff"){
     system("irsend SEND_ONCE ledstripe on");
     system("irsend SEND_ONCE ledstripe blue");
  }
  if (Value("RGB") eq "RGB ffff00"){
     system("irsend SEND_ONCE ledstripe on");
     system("irsend SEND_ONCE ledstripe yellow");
  }
  if (Value("RGB") eq "RGB ff00ff"){
     system("irsend SEND_ONCE ledstripe on");
     system("irsend SEND_ONCE ledstripe purple");
  }
  if (Value("RGB") eq "RGB 00FFFF"){
     system("irsend SEND_ONCE ledstripe on");
     system("irsend SEND_ONCE ledstripe cyan");
  }
