     system("irsend SEND_ONCE ledstripe on");
  }
  if (Value("RGB") eq "Aus"){
     system("irsend SEND_ONCE ledstripe off");
  }
  if (Value("RGB") ne "Aus"){
     fhem "set RGB on";
  }
  if (Value("RGB") ne "Up"){
     #system("irsend SEND_ONCE ledstripe on");
     system("irsend SEND_ONCE ledstripe up");
  }
