ROUTER:urDownstreamDSLRate:.* {
	my $ICON = "i8833";
	my $download = ReadingsVal("ROUTER","urDownstreamDSLRate","0");
	my $upload = ReadingsVal("ROUTER","urUpstreamDSLRate","0");
	my $DOWNLOAD = sprintf "%.1f",($download/1024),0;
	$DOWNLOAD =~ s/.0//g;
	my $UPLOAD = sprintf "%.1f",($upload/1024),0;
	$UPLOAD =~ s/.0//g;
	fhem ("setreading INFO Line5 DSL: ".$DOWNLOAD." / ".$UPLOAD." MBit/s");
	fhem ("setreading INFO Line5_icon ".$ICON);
}

ROUTER:mac_00_14_FD_15_90_04:.*|ROUTER:mac_90_E6_BA_D7_8F_6E:.*|ROUTER:mac_E0_3F_49_11_0A_CD:.* {
