		$TEXT = ReadingsVal("KODI", "label", "")." (".ReadingsVal("KODI", "year", "").")";
		fhem "set LAMETRIC msg 'i10125' ' $TEXT';";
		if (((Value("Tageslicht") eq "dunkel") || (Value("Belichtung") eq "dunkel")) && (ReadingsVal("KODI", "type", "") eq "unknown") && (Value("Automatik") eq "Ein") && ((ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Morgen")||(ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Abend")||(ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Nacht"))){
			if (ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Abend"){
				fhem("set LED0,LED1 HSV 240,100,100 1; set LED4 HSV 240,100,50 1");
				fhem("setstate LEDSWZ on");
				if (ReadingsVal("LD382A","state","") ne "off"){
					fhem("set LD382A 0");
				}
				if (ReadingsVal("Ambilight","state","") ne "on"){
					fhem("set Ambilight Ein");
				}
            	fhem("set Hyperion clearall");
        	}
        	else {
            	fhem("set LED0,LED1 HSV 240,100,50 1; set LED4 HSV 240,100,25 1");
				fhem("setstate LEDSWZ on");
				if (ReadingsVal("LD382A","state","") ne "off"){
					fhem("set LD382A 0");
				}
				if (ReadingsVal("Ambilight","state","") ne "on"){
					fhem("set Ambilight Ein");
				}
            	fhem("set Hyperion clearall");
			}
		}
		else {
			if ((Value("Automatik") eq "Ein") && (ReadingsVal("KODI", "type", "") eq "movie")) {
				if (ReadingsVal("Ambilight","state","") ne "on"){
					fhem("set Ambilight Ein");
				}
				fhem("set LED0,LED1,LED4 HSV 0,0,0 1");
				fhem("setstate LEDSWZ off");
				if (ReadingsVal("LD382A","state","") ne "off"){
					fhem("set LD382A 0");
				}
				fhem("set Hyperion clearall");
			}
		}
	}

	if ($EVENT eq "PAUSE"){
		if (((Value("Tageslicht") eq "dunkel") || (Value("Belichtung") eq "dunkel")) && (ReadingsVal("KODI", "playStatus", "") eq "paused") && (Value("Automatik") eq "Ein") && ((ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Morgen")||(ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Abend"))){
				if (ReadingsVal("Ambilight","state","") ne "on"){
					fhem("set Ambilight Ein");
				}
			fhem("set LED0,LED1 HSV 0,0,100 1; set LED4 HSV 0,0,50 1");
			fhem("setstate LEDSWZ on");
			if ((ReadingsVal("LD382A","state","") ne "on") || (ReadingsVal("LEDSWZ","brightness","") ne "50")){
				fhem("set LD382A 50");
			}
			fhem("set Hyperion on");
			fhem("set Hyperion rgb ffffff");
		}
		else {
			if (Value("Automatik") eq "Ein") {
				if ((ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Nacht") && (ReadingsVal("KODI", "playStatus", "") eq "paused")){
					if (ReadingsVal("Ambilight","state","") ne "on"){
						fhem("set Ambilight Ein");
					}
					fhem("set LED0,LED1 HSV 0,0,50 1; set LED4 HSV 0,0,25 1");
					fhem("setstate LEDSWZ on");
					if (ReadingsVal("LD382A","state","") ne "off"){
						fhem("set LD382A 0");
					}
					fhem("set Hyperion on");
					fhem("set Hyperion rgb ffffff");
				}
				else {
					if (ReadingsVal("KODI", "playStatus", "") eq "paused" ){
						if (ReadingsVal("Ambilight","state","") ne "on"){
							fhem("set Ambilight Ein");
						}
						fhem("set LED0,LED1,LED4 HSV 0,0,0 1");
						fhem("setstate LEDSWZ on");
						if (ReadingsVal("LD382A","state","") ne "off"){
							fhem("set LD382A 0");
						}
						fhem("set Hyperion clearall");
					}
				}
			}
		}
	}

	if ($EVENT eq "STOP"){
		if (((Value("Tageslicht") eq "dunkel") || (Value("Belichtung") eq "dunkel")) && (ReadingsVal("KODI", "playStatus", "") eq "stopped") && (Value("Automatik") eq "Ein") && ((ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Morgen")||(ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Abend"))){
			if (ReadingsVal("Ambilight","state","") ne "off"){
				fhem("set Ambilight Aus");
			}
			fhem("set LED0,LED1 HSV 0,0,100 1; set LED4 HSV 0,0,50 1");
			if ((ReadingsVal("LD382A","state","") ne "on") || (ReadingsVal("LEDSWZ","brightness","") ne "100")){
				fhem("set LD382A 100");
			}
		}
		else {
			if (Value("Automatik") eq "Ein") {
				if ((ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Nacht") && (ReadingsVal("KODI", "playStatus", "") eq "stopped")){
					if (ReadingsVal("Ambilight","state","") ne "off"){
						fhem("set Ambilight Aus");
					}
					fhem("set LED0,LED1 HSV 0,0,50 1; set LED4 HSV 0,0,25 1");
					if (ReadingsVal("LD382A","state","") ne "off"){
						fhem("set LD382A 0");
					}
					fhem("set Hyperion clearall");
				}
				else {
					if (ReadingsVal("KODI", "playStatus", "") eq "stopped" ){
						fhem("set LED0,LED1,LED4 HSV 0,0,0 1");
						if (ReadingsVal("LD382A","state","") ne "off"){
							fhem("set LD382A 0");
						}
						fhem("set Hyperion clearall");
						if (ReadingsVal("Ambilight","state","") ne "off"){
							fhem("set Ambilight Aus");
						}
					}
				}
			}
		}
	}
	if (Value("Fun") ne "Ein") {
		fhem ('define GETLEDValues at +00:00:02 {getLEDValues("LED0","LEDSWZ")};');
	}
 }
 else {
	fhem("set LED0,LED1,LED4 HSV 0,0,0 1");
	if (ReadingsVal("LEDSWZ","state","") ne "off"){
		fhem("set LD382A 0");
	}
	fhem("set Hyperion clearall");
	if (ReadingsVal("Ambilight","state","") ne "off"){
		fhem("set Ambilight Aus");
	}
 }
}

Fritzcall:event:.* {
       if ((ReadingsVal("Fritzcall","event",'') eq 'ring') && (ReadingsVal("Fritzcall","direction",'') eq 'incoming')) {
          fhem('set Hyperion effect Rainbow_swirl_fast;');
       }
       if ((ReadingsVal("Fritzcall","event",'') eq 'disconnect')) {
          fhem('set Hyperion clearall;');
       }

}

TEMPCONTROL:.* {
	my $ICON;
	my $TEMP = ReadingsVal("TEMPCONTROL","state","0");
	$TEMP+=0;
	my @a = split(/\ /,ReadingsVal("INFO","Line3","0"));
	my $OLDTEMP = $a[0];
	$OLDTEMP+=0;
	fhem ("setreading INFO teststring ".$OLDTEMP." ".$TEMP);
	if ($TEMP gt ($OLDTEMP)){
		$ICON ="i2369";
	}
	elsif ($TEMP lt $OLDTEMP){
		$ICON ="i8478";
	}
	else {
		$ICON ="i12211";
	}
	my $LINE3 = sprintf "%.1f C", $TEMP;
	fhem ("setreading INFO Line3 ".$LINE3);
	fhem ("setreading INFO Line3_icon ".$ICON);
}

GPIO4_DS18B20_000004f8b9a5:.* {
	my $Temp = ((ReadingsVal('GPIO4_DS18B20_000004f8b9a5','temperature','') + ReadingsVal('GPIO4_DS18B20_000004f92853','temperature',''))/2);
	my $Temp2 = sprintf "%.1f",$Temp,0;
	fhem 'set LCD clear';
	fhem 'set LCD writeXY 0,0,11 Temperatur:';
	fhem 'set LCD writeXY 0,1,15 Wohnung: '.$Temp2.' C';
	fhem "set TEMPCONTROL $Temp2";
	if ((Value("Automatik") eq "Ein") && (Value("LUEFTERAUTOMATIK") eq "Ein")) {
		if (($Temp2 gt ReadingsVal('TEMPMAX','state','')) && (ReadingsVal('LUEFTER_REMOTE','state','') ne 'on')) {
			fhem "set LUEFTER_REMOTE on";
		}
		if (($Temp+0.5 lt ReadingsVal('TEMPMAX','state','')) && (ReadingsVal('LUEFTER_REMOTE','state','') ne 'off')) {
			fhem "set LUEFTER_REMOTE off";
		}
		my $Tempdiff = $Temp - ReadingsVal('TEMPMAX','state','');
		fhem "setreading TEMPMAX difference $Tempdiff";
		if ($Tempdiff >= 1.5) {
			if (ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Nacht"){
				if (ReadingsVal("LUEFTER_REMOTE", "speed", "") ne "1"){
					fhem "set LUEFTER_REMOTE STUFE1";
				}
			}
			else {
				if (ReadingsVal("LUEFTER_REMOTE", "speed", "") ne "3"){
					fhem "set LUEFTER_REMOTE STUFE3";
				}
			}
			if (ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Nacht"){
				if (ReadingsVal('LUEFTER_REMOTE','mode','') ne 'night') {
					fhem "sleep 1;set LUEFTER_REMOTE NACHT";
				}
			}
			else {
				if (ReadingsVal('LUEFTER_REMOTE','mode','') ne 'day') {
					fhem "sleep 1;set LUEFTER_REMOTE TAG";
				}
			}
		}
		if (($Tempdiff < 1.5) && ($Tempdiff >= 0.75)) {
			if (ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Nacht"){
				if (ReadingsVal("LUEFTER_REMOTE", "speed", "") ne "1"){
					fhem "set LUEFTER_REMOTE STUFE1";
				}
			}
			else {
				if (ReadingsVal("LUEFTER_REMOTE", "speed", "") ne "2"){
					fhem "set LUEFTER_REMOTE STUFE2";
				}
			}
			if (ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Nacht"){
				if (ReadingsVal('LUEFTER_REMOTE','mode','') ne 'night') {
					fhem "sleep 1;set LUEFTER_REMOTE NACHT";
				}
			}
			else {
				if (ReadingsVal('LUEFTER_REMOTE','mode','') ne 'day') {
					fhem "sleep 1;set LUEFTER_REMOTE TAG";
				}
			}
		}
		if (($Tempdiff < 0.75) && ($Tempdiff > 0)) {
			fhem "set LUEFTER_REMOTE STUFE1";
			if (ReadingsVal("AUTOMATIKFENSTER", "state", "") eq "Nacht"){
				if (ReadingsVal('LUEFTER_REMOTE','mode','') ne 'night') {
					fhem "sleep 1;set LUEFTER_REMOTE NACHT";
				}
			}
			else {
				if (ReadingsVal('LUEFTER_REMOTE','mode','') ne 'day') {
					fhem "sleep 1;set LUEFTER_REMOTE TAG";
				}
			}
		}
	}
	if ((Value("Automatik") eq "Ein") && (Value("KLIMAAUTOMATIK") eq "Ein")) {
		if ($Temp2 > ReadingsVal('TEMPMAX','state','')) {
			if (Value("GLOBALANWESENHEIT") eq "present"){
				fhem "sleep 1; set KLIMA_REMOTE on";
			}
		}
		else {
			if ($Temp2+0.25 < ReadingsVal('TEMPMAX','state','')) {
				fhem "sleep 1; set KLIMA_REMOTE off";
			}
		}
  	}
	# Overheating
	if ((Value("Automatik") eq "Ein") && (Value("KLIMAAUTOMATIK") eq "Aus")) {
		if ($Temp2 > ReadingsVal('TEMPOVER','state','0')) {
				fhem "sleep 1; set KLIMA_REMOTE on";
				fhem "set LAMETRIC msg 'a9275' ' Alarm: Ãberhitzung!';";
		}
		else {
			if ($Temp2 < ReadingsVal('TEMPMAX','state','0')) {
				fhem "sleep 1; set KLIMA_REMOTE off";
			}
		}
  	}
	# End Overheat
}

TRIGGER1 {
	if ($EVENT eq "on"){
		fhem("set LEDSWZ RGB FFFFFF");
		fhem("set LD382A on");
		fhem("set Ambilight on");
		fhem ('define TRIGGER1Reset at +00:00:02 {fhem("set TRIGGER1 none")};');
	}
	if ($EVENT eq "off"){
		fhem("set LEDSWZ 0");
		fhem("set LD382A off");
		fhem("set Ambilight off");
		fhem ('define TRIGGER1Reset at +00:00:02 {fhem("set TRIGGER1 none")};');
	}
}

FahrzeitArbeit:.* {
         my @a = split(/\ /,ReadingsVal("HOME2WORK", "delay", ''));
         if ($a[0] >= 5){
            fhem('set VERKEHR on');
            fhem('set Alarm on; sleep 0.25; set Alarm off; sleep 0.25; set Alarm on; sleep 0.25; set Alarm off;');
         }
         if ($a[0] < 5){
            fhem('set VERKEHR off');
         }
}

