        my $temperature = sprintf("%.1f",ReadingsVal("GPIO4_DS18B20_000004f8b9a5", "temperature", 0));
        my $temperature1 = sprintf("%.1f",ReadingsVal("GPIO4_DS18B20_000004f91a72", "temperature", 0));
        my $temperature2 = sprintf("%.1f",ReadingsVal("GPIO4_DS18B20_000004f92d8b", "temperature", 0));
        fhem("set $NAME send Temperatur (Wohnzimmer): $temperature Â°C");
        fhem("set $NAME send Temperatur (RaspberryPi): $temperature1 Â°C");
        fhem("set $NAME send Temperatur (Aquarium): $temperature2 Â°C");
    }
    else {
        fhem("set $NAME send Befehl nicht bekannt");
    }
}

WAMHillesheim:message:.* {
    fhem("set WAMHillesheimLastMessage $EVENT");
    if (lc $EVTPART1 eq lc 'licht') {
        if (lc $EVENT =~ lc 'an') {
            fhem("set Beleuchtung Ein");
            fhem("set $NAME send Befehl \"Licht an\" ausgefuehrt");
        }
        elsif (lc $EVENT =~ lc 'aus') {
            fhem("set Beleuchtung Aus");
            fhem("set $NAME send Befehl \"Licht aus\" ausgefuehrt");
        }
    }
    elsif ((lc $EVTPART1 eq lc 'hallo') || (lc $EVTPART1 eq lc 'test') || (lc $EVTPART1 eq lc 'status')) {
            fhem("set $NAME send Ich bin online");
    }
    elsif (lc $EVTPART1 eq lc 'nacht') {
            fhem("set Beleuchtung Aus");
            fhem("set $NAME send Befehl \"Licht aus\" ausgefuehrt")
    }
    elsif (lc $EVTPART1 eq lc 'film') {
            fhem("set Film push");
            fhem("set $NAME send Befehl \"Beleuchtung Film\" ausgefuehrt")
    }
    elsif (lc $EVTPART1 eq lc 'serie') {
            fhem("set Serie push");
            fhem("set $NAME send Befehl \"Beleuchtung Serie\" ausgefuehrt")
    }
    elsif (lc $EVTPART1 eq lc 'musik') {
            fhem("set Musik push");
            fhem("set $NAME send Befehl \"Beleuchtung Musik\" ausgefuehrt")
    }
    elsif (lc $EVTPART1 eq lc 'tv') {
            fhem("set TV push");
            fhem("set $NAME send Befehl \"Beleuchtung TV\" ausgefuehrt")
    }

    elsif ((lc $EVTPART1 eq lc 'temperatur') || (lc $EVTPART1 eq lc 'temp')) {
        my $temperature = sprintf("%.1f",ReadingsVal("GPIO4_DS18B20_000004f8b9a5", "temperature", 0));
        my $temperature1 = sprintf("%.1f",ReadingsVal("GPIO4_DS18B20_000004f91a72", "temperature", 0));
        my $temperature2 = sprintf("%.1f",ReadingsVal("GPIO4_DS18B20_000004f92d8b", "temperature", 0));
        fhem("set $NAME send Temperatur (Wohnzimmer): $temperature Â°C");
        fhem("set $NAME send Temperatur (RaspberryPi): $temperature1 Â°C");
        fhem("set $NAME send Temperatur (Aquarium): $temperature2 Â°C");
    }
    else {
        fhem("set $NAME send Befehl nicht bekannt");
    }
}

