YAMAHA_REMOTE {
   if ("$EVENT" eq "KODI"){
      $EVENT = "BDDVD";
   }
	fhem 'set LCD clear';
	fhem 'set LCD writeXY 0,0,12 VERSTAERKER:';
	fhem 'set LCD writeXY 0,1,'.length($EVENT).' '.$EVENT;
   if (Value("YAMAHA_REMOTE") eq "Ein"){
       system("irsend SEND_ONCE YAMAHA on");
	   fhem("setstate YAMAHA_REMOTE on");
   }
   if (Value("YAMAHA_REMOTE") ne "Aus"){
       system("irsend SEND_ONCE YAMAHA on");
	   fhem("setstate YAMAHA_REMOTE Ein");
	   my @input = split(/\ /,$EVENT);
	   my $sendkey;
	   if (length($input[1] ||= '')){
	   		$sendkey = $input[1];
	   }
	   else {
	   	   	$sendkey = $input[0];
	   }
	   if (($sendkey ne "on") && ($sendkey ne "off") && ($sendkey ne "Ein") && ($sendkey ne "Aus")) {
	   		fhem("setreading YAMAHA_REMOTE input $sendkey");
	   }
       system("sleep 1;irsend SEND_ONCE YAMAHA $sendkey");
   }
   if (Value("YAMAHA_REMOTE") eq "Aus"){
       system("irsend SEND_ONCE YAMAHA off");
       fhem("setstate YAMAHA_REMOTE Aus");
   }
}

